mapper.select:
       select * from (
          select b.*,rownum rnum from (
		    select a.* from (
		      select n.notice_num, n.notice_title, n.notice_content, n.notice_regdate, s.sort_num, s.sort_name, m.member_num
		          from notice n, sort s, member m
	              where n.sort_num = s.sort_num
		          and n.member_num = m.member_num
		          order by n.notice_num desc
		          ) a  
		      ) b 
		 )   where rnum>=#{startRow} and rnum<=#{endRow}

mapper.delete:
   delete from (
   select n.*,s.sort_name
	     from notice n, sort s
	        where n.sort_num = s.sort_num
	   ) where notice_num=#{notice_num}

mapper.select:
   select a.* from ( 
	   select n.notice_title, n.notice_content,n.notice_num,s.sort_name
	     from notice n, sort s
	        where n.sort_num = s.sort_num
	   ) a
	where notice_num = #{notice_num}
