mapper.select:
select * from (
    select r.*,rownum rnum from (
        select b.booking_num,b.regdate,sh.show_thumbnail,sh.show_name,h.hall_name,
        sc.schedule_date,sc.schedule_time,b.booking_seat,b.booking_price,p.payment_status,sh.show_num
        from booking b
        join payment p on (b.booking_num = p.booking_num)
        join schedule sc on (b.schedule_num = sc.schedule_num)
        join show sh on (sc.show_num = sh.show_num)
        join hall h on (sh.hall_num = h.hall_num)
        where b.member_num = #{member_num}
        and b.regdate between #{startdate} and #{enddate}
        order by b.booking_num desc
    )r
)where rnum between #{startRow} and #{endRow}

select sc.schedule_date from booking b
join schedule sc on (b.schedule_num = sc.schedule_num)
where booking_num = #{booking_num}

SELECT * FROM (SELECT a.*, ROWNUM rn FROM ( SELECT b.booking_num bookingNum, member_id memberId, sh.show_name showName, sc.schedule_date scheduleDate, sc.schedule_time scheduleTime, b.booking_price bookingPrice, TO_CHAR(b.regdate, 'YYYY-MM-DD') bookingDate, p.payment_status status FROM booking b INNER JOIN payment p ON (b.booking_num = p.booking_num) INNER JOIN member m ON b.member_num = m.member_num INNER JOIN schedule sc ON (b.schedule_num = sc.schedule_num) INNER JOIN show sh ON (sc.show_num = sh.show_num)
${field} LIKE '%'||#{search}||'%'
p.payment_status = #{status}
AND ${field} LIKE '%'||#{search}||'%'
)a ) WHERE rn BETWEEN #{startRow} AND #{endRow}
